CREATE OR REPLACE FUNCTION pls_obter_med_exec_ocor ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, ie_tipo_item_p bigint, cd_medico_p text, ie_medico_exec_solic_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
nr_sequencia_w		bigint;


BEGIN

/*Verifica se é o médico executor da conta*/

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_p
and	(((cd_medico_executor = cd_medico_p) and (ie_medico_exec_solic_p in ('E','A')))
or	 ((cd_medico_solicitante = cd_medico_p) and (ie_medico_exec_solic_p in ('S','A'))));

/*Se não for o prestador da conta e o tipo o tipo da regra for executante verifica-se os participantes*/

if	((ie_medico_exec_solic_p in ('E','A')) and (coalesce(nr_sequencia_w,0) = 0)) then

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	pls_proc_participante a,
		pls_conta_proc b
	where	b.nr_sequencia = a.nr_seq_conta_proc
	and	((b.nr_seq_conta = nr_seq_conta_p AND ie_tipo_item_p = '8') or
		 (b.nr_sequencia = nr_seq_conta_proc_p AND ie_tipo_item_p = '3'))
	and	a.cd_medico = cd_medico_p;

end if;

if (coalesce(nr_sequencia_w,0) > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
