SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mensagem_importacao (nr_seq_protocolo_p bigint, ie_tipo_mensagem_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_tipo_mensagem_p:
'E' - Erro
'T' - Tiss
*/
ds_retorno_w varchar(2000) := '';

BEGIN
	if ( ie_tipo_mensagem_p = 'T' ) then
		select	substr(ds_motivo_tiss, 1, 255)
		into STRICT	ds_retorno_w
		from	tiss_motivo_glosa
		where	cd_motivo_tiss = to_char(( SELECT	max(nr_seq_mensagem)
						   from		pls_mensagem_importacao
						   where	nr_seq_protocolo = nr_seq_protocolo_p));
	elsif ( ie_tipo_mensagem_p = 'E' ) then
		select	ds_erro
		into STRICT	ds_retorno_w
		from	pls_mensagem_importacao
		where	nr_seq_protocolo = nr_seq_protocolo_p;
	end if;
	return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
