SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mensagem_quitacao (nr_titulo_p titulo_receber.nr_titulo%type) RETURNS varchar AS $body$
DECLARE


nr_seq_mensalidade_w		titulo_receber.nr_seq_mensalidade%type;
ie_origem_titulo_w		titulo_receber.ie_origem_titulo%type;
nr_seq_pagador_w		titulo_receber.nr_seq_pagador%type;
ds_mensagem_w			varchar(4000);
qt_registros_w			integer;
dt_referencia_w			timestamp;
dt_ano_w			timestamp;
dt_ano_final_w			timestamp;


BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	a.nr_seq_mensalidade,
		a.ie_origem_titulo,
		a.nr_seq_pagador
	into STRICT	nr_seq_mensalidade_w,
		ie_origem_titulo_w,
		nr_seq_pagador_w
	from	titulo_receber a
	where	a.nr_titulo = nr_titulo_p;

	if (nr_seq_mensalidade_w IS NOT NULL AND nr_seq_mensalidade_w::text <> '') then
		select	b.dt_referencia
		into STRICT	dt_referencia_w
		from	pls_mensalidade			b,
			titulo_receber			a
		where	a.nr_seq_mensalidade	= b.nr_sequencia
		and	a.nr_titulo		= nr_titulo_p;
	else
		select	a.dt_emissao
		into STRICT	dt_referencia_w
		from	titulo_receber			a
		where	a.nr_titulo		= nr_titulo_p;
	end if;

	if (to_char(dt_referencia_w,'mm') = '05') then
			dt_ano_w		:= to_date('01/01'||to_char(add_months(dt_referencia_w,-12),'yyyy'));
			dt_ano_final_w		:= to_date('31/12'||to_char(add_months(dt_referencia_w,-12),'yyyy'));

			select	count(1)
			into STRICT	qt_registros_w
			
			where	exists (SELECT	1
					from	titulo_receber
					where	coalesce(dt_liquidacao::text, '') = ''
					and	dt_vencimento between dt_ano_w and dt_ano_final_w
					and	ie_origem_titulo in ('3','17','18')
					and	nr_seq_pagador = nr_seq_pagador_w);

			if (qt_registros_w = 0) then
				ds_mensagem_w := 'A Unimed São José do Rio Preto Cooperativa de Trabalho Medico, em cumprimento a Lei Federal número 12.007/09, declara que as faturas deste plano de saúde, vencidas  no  ano  de ' || to_char(dt_ano_w,'yyyy')||', foram quitadas.'||
						'Esta declaração substitui, para  a comprovação  do  cumprimento  das  obrigações  do consumidor, as quitações das faturas vencidas em '|| to_char(dt_ano_w,'yyyy')||'.'||
						'Este documento não quita parcelamentos, cobranças judiciais,  serviços prestados e não faturados e outras exceções previstas na Lei. ';
			end if;
	end if;
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_mensagem_quitacao (nr_titulo_p titulo_receber.nr_titulo%type) FROM PUBLIC;

