SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mensagem_transf ( nr_sq_proposta_p bigint, nr_seq_beneficiario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_mensagem_w		varchar(255);
ie_tipo_contratacao_w	varchar(2);
ie_tipo_contr_plano_w	varchar(2);
nr_seq_plano_w		bigint;



BEGIN

select	ie_tipo_contratacao
into STRICT	ie_tipo_contratacao_w
from	pls_proposta_adesao
where	nr_sequencia = nr_sq_proposta_p;

if (ie_tipo_contratacao_w = 'I') then
	select	nr_seq_plano
	into STRICT	nr_seq_plano_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_beneficiario_p;

	select	ie_tipo_contratacao
	into STRICT	ie_tipo_contr_plano_w
	from	pls_plano
	where	nr_sequencia = nr_seq_plano_w;

	if (ie_tipo_contr_plano_w = 'CE') then
		ds_mensagem_w	:= 'Empresarial para Individual/Familiar';
	end if;

end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
