CREATE OR REPLACE FUNCTION pls_obter_mensaliades_aberto ( nr_seq_pagador_p bigint) RETURNS bigint AS $body$
DECLARE


qt_mensalidades_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_mensalidades_w
from	titulo_receber			c,
	pls_mensalidade			b
where	c.nr_seq_mensalidade	= b.nr_sequencia
and	coalesce(b.ie_cancelamento::text, '') = ''
and	b.nr_seq_pagador	= nr_seq_pagador_p
and	c.dt_pagamento_previsto	< clock_timestamp()
and	((c.ie_situacao	= '1') or (coalesce(c.ie_situacao::text, '') = ''));


return	qt_mensalidades_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_mensaliades_aberto ( nr_seq_pagador_p bigint) FROM PUBLIC;

