CREATE OR REPLACE FUNCTION pls_obter_mensalidade_gerada ( nr_seq_pagador_p bigint, nr_seq_segurado_p bigint, dt_competencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_mensalidade
	where	nr_seq_pagador	= nr_seq_pagador_p
	and	trunc(dt_referencia,'Month') = trunc(dt_competencia_p,'Month')
	and	coalesce(ie_cancelamento::text, '') = '';

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
elsif (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_mensalidade_segurado	b,
		pls_mensalidade			a
	where	b.nr_seq_mensalidade	= a.nr_sequencia
	and	b.nr_seq_segurado	= nr_seq_segurado_p
	and	trunc(dt_mesano_referencia,'Month') = trunc(dt_competencia_p,'Month')
	and	coalesce(ie_cancelamento::text, '') = '';

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
