CREATE OR REPLACE FUNCTION pls_obter_mens_externa_glosa (nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_menssagem_w		varchar(255);

C01 CURSOR FOR
	SELECT	b.ds_mensagem_externa
	from	pls_msg_externa_glosa	b,
		pls_guia_glosa	a
	where	b.nr_seq_motivo_glosa	=  a.nr_seq_motivo_glosa
	and	b.ie_situacao		= 'A'
	and (a.nr_seq_guia		= nr_seq_guia_p
	or	a.nr_seq_guia_proc(	SELECT	x.nr_sequencia
						from	pls_guia_plano_proc x
						where	x.nr_seq_guia	= nr_seq_guia_p)
	or	a.nr_seq_guia_mat	in (	select	y.nr_sequencia
						from	pls_guia_plano_mat y
						where	y.nr_seq_guia	= nr_seq_guia_p));


BEGIN

open C01;
loop
fetch C01 into
	ds_menssagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_menssagem_w;
	else
		ds_retorno_w	:= ds_retorno_w||chr(13)||ds_menssagem_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_mens_externa_glosa (nr_seq_guia_p bigint) FROM PUBLIC;

