CREATE OR REPLACE FUNCTION pls_obter_mens_gerada_grupo ( dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_grupo_contrato_p pls_grupo_contrato.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_pagador_w	bigint;
qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_pagador
	from	pls_contrato_pagador a,
		pls_contrato_grupo b
	where	a.nr_seq_contrato 	= b.nr_seq_contrato
	and	b.nr_seq_grupo		= nr_seq_grupo_contrato_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_mensalidade		b,
		pls_lote_mensalidade	a
	where	a.nr_sequencia = b.nr_seq_lote
	and	coalesce(b.ie_cancelamento::text, '') = ''
	and	b.nr_seq_pagador = nr_seq_pagador_w
	and	dt_mesano_referencia between dt_inicio_p and coalesce(dt_fim_p,dt_mesano_referencia);
	
	if (qt_registros_w > 0) then
		goto final;
	end if;
	end;
end loop;
close C01;

qt_registros_w	:= coalesce(qt_registros_w,0);

<<final>>
return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
