SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mens_mes_anterior ( nr_seq_mensalidade_seg_p bigint) RETURNS bigint AS $body$
DECLARE


dt_mensalidade_w		timestamp;
vl_mensalidade_ant_w		double precision;
nr_seq_segurado_w		bigint;


BEGIN

select 	dt_mesano_referencia,
	nr_seq_segurado
into STRICT	dt_mensalidade_w,
	nr_seq_segurado_w
from	pls_mensalidade_segurado
where	nr_sequencia = nr_seq_mensalidade_seg_p;

if (dt_mensalidade_w IS NOT NULL AND dt_mensalidade_w::text <> '') then
	select 	sum(vl_mensalidade)
	into STRICT	vl_mensalidade_ant_w
	from	pls_mensalidade_segurado
	where	dt_mesano_referencia 	= add_months(dt_mensalidade_w, -1)
	and	nr_seq_segurado 	= nr_seq_segurado_w;
end if;


return	vl_mensalidade_ant_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
