SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mens_pendentes_pag ( nr_seq_pagador_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_meses_pendente_w	bigint;
ds_retorno_w		varchar(255);

vl_mensalidade_w	double precision	:= 0;
dt_referencia_w		timestamp;
ds_referencia_w		varchar(400)	:= '';
vl_mens_w		double precision;

qt_total_w		bigint;
qt_linha_w		bigint;
ds_barra_w		varchar(3)	:= '';

C01 CURSOR FOR
	SELECT	b.dt_referencia,
		sum(b.vl_mensalidade),
		row_number() OVER () AS rownum
	from	titulo_receber			c,
		pls_mensalidade			b
	where	c.nr_seq_mensalidade	= b.nr_sequencia
	and	coalesce(b.ie_cancelamento::text, '') = ''
	and	b.nr_seq_pagador	= nr_seq_pagador_p
	and	trunc(b.dt_referencia,'month')	<> dt_referencia_p
	and	((c.ie_situacao	= '1') or (coalesce(c.ie_situacao::text, '') = ''))
	group by b.dt_referencia,
		rownum
	order by b.dt_referencia;


BEGIN

select	count(*)
into STRICT	qt_total_w
from	titulo_receber			c,
	pls_mensalidade			b
where	c.nr_seq_mensalidade	= b.nr_sequencia
and	coalesce(b.ie_cancelamento::text, '') = ''
and	b.nr_seq_pagador	= nr_seq_pagador_p
and	trunc(b.dt_referencia,'month')	<> dt_referencia_p
and	((c.ie_situacao	= '1') or (coalesce(c.ie_situacao::text, '') = ''));

if (qt_total_w > 0) then
	open C01;
	loop
	fetch C01 into
		dt_referencia_w,
		vl_mens_w,
		qt_linha_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		vl_mensalidade_w	:= coalesce(vl_mensalidade_w,0) + coalesce(vl_mens_w,0);

		if (coalesce(ds_referencia_w::text, '') = '') then
			ds_barra_w	:= '';
		else
			ds_barra_w	:= '/';
		end if;

		if (to_char(dt_referencia_w,'mm') = '12') then
			ds_referencia_w		:= ds_referencia_w || ds_barra_w || substr(to_char(dt_referencia_w,'month'),1,3)||to_char(dt_referencia_w,'yy');
		else
			ds_referencia_w		:= ds_referencia_w || ds_barra_w || substr(to_char(dt_referencia_w,'month'),1,3);
		end if;

		if	((qt_total_w = qt_linha_w) and (to_char(dt_referencia_w,'mm') = '12')) then
			ds_referencia_w		:= ds_referencia_w || ds_barra_w || to_char(dt_referencia_w,'yy');
		end if;

		end;
	end loop;
	close C01;

	ds_referencia_w	:= ds_referencia_w || '/' ||to_char(dt_referencia_p,'yy');
end if;

if (ie_opcao_p = 'R') then
	if (coalesce(ds_referencia_w::text, '') = '') then
		ds_retorno_w	:= 'Ult. Pgto.( OK ) ' || to_char(add_months(dt_referencia_p,-1),'month/yyyy');
	else
		ds_retorno_w	:= 'Ult. Pgto.( Mensalidades em aberto: '||ds_referencia_w||' ) ' || to_char(add_months(dt_referencia_p,-1),'month/yyyy');
	end if;
else
	ds_retorno_w	:= ds_referencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
