CREATE OR REPLACE FUNCTION pls_obter_meses_entre_datas (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_ano_inicial_w	bigint;
qt_ano_final_w		bigint;
qt_mes_inicial_w	bigint;
qt_mes_final_w		bigint;
qt_meses_w		bigint	:= 0;


BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	qt_ano_inicial_w	:= somente_numero(to_char(dt_inicial_p,'yyyy'));
	qt_ano_final_w		:= somente_numero(to_char(dt_final_p,'yyyy'));
	qt_mes_inicial_w	:= somente_numero(to_char(dt_inicial_p,'mm'));
	qt_mes_final_w		:= somente_numero(to_char(dt_final_p,'mm'));

	qt_meses_w	:= (qt_ano_final_w - qt_ano_inicial_w) * 12;

	qt_meses_w	:= (qt_meses_w - qt_mes_inicial_w) + qt_mes_final_w;
end if;

return	qt_meses_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
