SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_meses_pendentes (cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
dt_ref_mes_mens_w		timestamp;
ds_lista_w				varchar(255);

C01 CURSOR FOR 
	SELECT	distinct to_date(obter_dados_titulo_receber(b.nr_titulo,'MM')) 
	from		processo_cobranca a, 
				cobranca b 
	where		a.nr_sequencia	= b.nr_seq_processo 
	and		((a.cd_pessoa_fisica = cd_pessoa_fisica_p) or (a.cd_cgc = cd_cgc_p)) 
	and		b.ie_status	= 'P' 
	order by	to_date(obter_dados_titulo_receber(b.nr_titulo,'MM'));
	

BEGIN 
 
ds_lista_w := '';
 
open C01;
loop 
fetch C01 into	 
	dt_ref_mes_mens_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (coalesce(ds_lista_w::text, '') = '') then 
		ds_lista_w	:= to_char(dt_ref_mes_mens_w,'dd/mm/yyyy');
	else 
		ds_lista_w	:= replace(ds_lista_w,' e ',', ') || ' e ' || to_char(dt_ref_mes_mens_w,'dd/mm/yyyy');
	end if;
end loop;
close C01;
 
ds_retorno_w := ds_lista_w;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_meses_pendentes (cd_pessoa_fisica_p text, cd_cgc_p text) FROM PUBLIC;

