SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_meses_renovacao ( nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_renovacao_w	bigint;
nr_seq_contrato_w	bigint;
ie_contrato_w		varchar(2);
ie_tipo_segurado_w	varchar(10);
ie_tipo_contrato_w	varchar(10);
nr_seq_intercambio_w	bigint;
ie_titularidade_w	varchar(10);

C01 CURSOR FOR
	SELECT	qt_dias_renovacao
	from	pls_param_regra_carteira
	where	((ie_tipo_renovacao = 'R') or (coalesce(ie_tipo_renovacao::text, '') = ''))
	and	((ie_tipo_contrato = ie_tipo_contrato_w) or (ie_tipo_contrato = 'A'))
	and	((ie_contrato = ie_contrato_w) or (coalesce(ie_contrato::text, '') = ''))
	and	((coalesce(ie_titularidade,'A') = ie_titularidade_w) or (coalesce(ie_titularidade,'A') = 'A'))
	and	((ie_tipo_segurado = ie_tipo_segurado_w) or (coalesce(ie_tipo_segurado::text, '') = ''))
	order by coalesce(ie_tipo_renovacao, ' '),
		ie_tipo_contrato,
		CASE WHEN ie_tipo_segurado='' THEN -1  ELSE 1 END;


BEGIN

select	nr_seq_contrato,
	ie_tipo_segurado,
	nr_seq_intercambio,
	CASE WHEN coalesce(nr_seq_titular::text, '') = '' THEN 'T'  ELSE 'D' END
into STRICT	nr_seq_contrato_w,
	ie_tipo_segurado_w,
	nr_seq_intercambio_w,
	ie_titularidade_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

qt_dias_renovacao_w	:= 0;

if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	select	coalesce(max(qt_meses_renovacao),0)
	into STRICT	qt_dias_renovacao_w
	from    pls_carteira_renovacao
	where   nr_seq_contrato 	= nr_seq_contrato_w
	and	((ie_tipo_renovacao	= 'R')
	or (coalesce(ie_tipo_renovacao::text, '') = ''));

	select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_contrato_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_w;

	ie_contrato_w	:= 'O';
elsif (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
	select	coalesce(max(qt_meses_renovacao),0)
	into STRICT	qt_dias_renovacao_w
	from    pls_carteira_renovacao
	where   nr_seq_intercambio 	= nr_seq_intercambio_w
	and	((ie_tipo_renovacao	= 'R')
	or (coalesce(ie_tipo_renovacao::text, '') = ''));

	select	CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_contrato_w
	from	pls_intercambio
	where	nr_sequencia = nr_seq_intercambio_w;

	ie_contrato_w	:= 'I';
end if;

if (qt_dias_renovacao_w = 0) then
	open C01;
	loop
	fetch C01 into
		qt_dias_renovacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	coalesce(qt_dias_renovacao_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
