SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_meses_renov_inter ( nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_renovacao_w	bigint;
nr_seq_intercambio_w	bigint;


BEGIN

select	max(nr_seq_intercambio)
into STRICT	nr_seq_intercambio_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

select	coalesce(max(qt_meses_renovacao),0)
into STRICT	qt_dias_renovacao_w
from    pls_carteira_renovacao
where   nr_seq_intercambio 	= nr_seq_intercambio_w
and	((ie_tipo_renovacao	= 'R')
or (coalesce(ie_tipo_renovacao::text, '') = ''));

return	qt_dias_renovacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
