CREATE OR REPLACE FUNCTION pls_obter_mes_competencia ( nr_seq_competencia_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10);


BEGIN
select	to_char(dt_mes_competencia ,'mm/yyyy')
into STRICT	ds_retorno_w
from	pls_competencia
where	nr_sequencia = nr_seq_competencia_p
and	cd_estabelecimento	= cd_estabelecimento_p;

return	ds_retorno_w;

end;
$body$
