CREATE OR REPLACE FUNCTION pls_obter_mes_origem_lanc ( nr_seq_lancamento_p pls_segurado_mensalidade.nr_sequencia%type) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
nr_seq_item_origem_w	pls_segurado_mensalidade.nr_seq_item_origem%type;
nr_seq_reaj_retro_w	pls_segurado_mensalidade.nr_seq_reaj_retro%type;


BEGIN

select	nr_seq_item_origem,
	nr_seq_reaj_retro,
	dt_competencia_origem
into STRICT	nr_seq_item_origem_w,
	nr_seq_reaj_retro_w,
	dt_retorno_w
from	pls_segurado_mensalidade
where	nr_sequencia	= nr_seq_lancamento_p;

if (coalesce(dt_retorno_w::text, '') = '') then
	if (nr_seq_item_origem_w IS NOT NULL AND nr_seq_item_origem_w::text <> '') then
		select	b.dt_mesano_referencia
		into STRICT	dt_retorno_w
		from	pls_mensalidade_seg_item	a,
			pls_mensalidade_segurado	b
		where	b.nr_sequencia	= a.nr_seq_mensalidade_seg
		and	a.nr_sequencia	= nr_seq_item_origem_w;
	elsif (nr_seq_reaj_retro_w IS NOT NULL AND nr_seq_reaj_retro_w::text <> '') then
		select	dt_referencia_reajuste
		into STRICT	dt_retorno_w
		from	pls_reajuste_cobr_retro
		where	nr_sequencia	= nr_seq_reaj_retro_w;
	end if;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
