SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_mes_reaj_contrato ( nr_seq_contrato_p bigint, nr_seq_intercambio_p bigint, ie_formato_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15)	:= null;
dt_retorno_w		timestamp		:= null;


BEGIN

if (coalesce(nr_seq_contrato_p,0) <> 0) then
	begin
	select	max(b.dt_reajuste)
	into STRICT	dt_retorno_w
	from	pls_contrato_grupo	a,
		pls_grupo_contrato	b
	where	a.nr_seq_grupo		= b.nr_sequencia
	and	a.nr_seq_contrato	= nr_seq_contrato_p
	and	a.ie_reajuste_grupo	= 'S'
	and	b.ie_situacao		= 'A';
	exception
	when others then
		dt_retorno_w := null;
	end;

	if (coalesce(dt_retorno_w::text, '') = '') then
		begin
		select	max(a.dt_reajuste)
		into STRICT	dt_retorno_w
		from	pls_contrato	a
		where	a.nr_sequencia	= nr_seq_contrato_p;
		exception
		when others then
			dt_retorno_w := null;
		end;

		if (coalesce(dt_retorno_w::text, '') = '') then
			begin
			select	add_months(a.dt_contrato,coalesce(qt_intervalo,12))
			into STRICT	dt_retorno_w
			from	pls_contrato	a
			where	a.nr_sequencia	= nr_seq_contrato_p;
			exception
			when others then
				dt_retorno_w := null;
			end;
		end if;
	end if;
elsif (coalesce(nr_seq_intercambio_p,0) <> 0) then
	select	max(b.dt_reajuste)
	into STRICT	dt_retorno_w
	from	pls_regra_grupo_inter	b,
		pls_intercambio		a
	where	a.nr_seq_grupo_intercambio	= b.nr_sequencia
	and	a.nr_sequencia			= nr_seq_intercambio_p;

	if (coalesce(dt_retorno_w::text, '') = '') then
		select	max(dt_inclusao)
		into STRICT	dt_retorno_w
		from	pls_intercambio
		where	nr_sequencia = nr_seq_intercambio_p;
	end if;
end if;

if (dt_retorno_w IS NOT NULL AND dt_retorno_w::text <> '') then
	if (ie_formato_p = 'E') then
		ds_retorno_w := initcap(to_char(dt_retorno_w,'month'));
	elsif (ie_formato_p = 'MM') then
		ds_retorno_w := to_char(dt_retorno_w,'MM');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
