CREATE OR REPLACE FUNCTION pls_obter_meta_vendedor_mens ( nr_seq_vendedor_p bigint, dt_mes_referencia_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p:
	D - Dia limite
	V - Meta por vidas
	F - Meta por faturamento
*/
ds_retorno_w			double precision;
nr_seq_vendedor_meta_w		bigint;
dt_limite_movimentacao_w	bigint;
qt_meta_w			bigint;
vl_meta_faturamento_w		double precision;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_vendedor_meta_w
from	pls_vendedor_meta
where	nr_seq_vendedor = nr_seq_vendedor_p
and	trunc(dt_mesano_referencia,'month') = trunc(dt_mes_referencia_p,'month');

select	dt_limite_movimentacao,
	qt_meta,
	vl_meta_faturamento
into STRICT	dt_limite_movimentacao_w,
	qt_meta_w,
	vl_meta_faturamento_w
from	pls_vendedor_meta
where	nr_sequencia = nr_seq_vendedor_meta_w;

if (ie_opcao_p = 'D') then
	ds_retorno_w := dt_limite_movimentacao_w;
elsif (ie_opcao_p = 'V') then
	ds_retorno_w := qt_meta_w;
elsif (ie_opcao_p = 'F') then
	ds_retorno_w := vl_meta_faturamento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
