SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_metodo_transmissao ( nr_seq_solicitacao_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_pessoa_fisica_w		pls_solicitacao_cns.cd_pessoa_fisica%type;
nm_mae_w			compl_pessoa_fisica.nm_contato%type;
nr_cpf_w			pessoa_fisica.nr_cpf%type;
nm_pessoa_fisica_w		pessoa_fisica.nm_pessoa_fisica%type;
id_retorno_w			smallint;


BEGIN

id_retorno_w := 1;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pls_solicitacao_cns
where	nr_sequencia		= nr_seq_solicitacao_p
and	cd_estabelecimento	= cd_estabelecimento_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	begin

	select    max(nm_contato)
	into STRICT	  nm_mae_w
	from      compl_pessoa_fisica
	where     cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and       ie_tipo_complemento 	= 5;

	exception
	when others then
		nm_mae_w := null;
	end;

	begin
	select	nr_cpf,
		nm_pessoa_fisica
	into STRICT	nr_cpf_w,
		nm_pessoa_fisica_w
	from	pessoa_fisica
	where	cd_pessoa_fisica 	= cd_pessoa_fisica_w;
	exception
	when others then
		nr_cpf_w 		:= null;
		nm_pessoa_fisica_w 	:= null;
	end;

	--CPF
	if (obter_se_cpf_valido(nr_cpf_w) = 'S') then
		id_retorno_w := 0;

	--Nome completo e nome da mãe
	elsif (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') and (nm_mae_w IS NOT NULL AND nm_mae_w::text <> '') then
		id_retorno_w := 2;

	--Nome completo
	elsif (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') then
		id_retorno_w := 1;

	end if;
	--Código 3 seria Consulta por Nome Completo e Nome Completo da Mae (Identica ou Aproximada), mas não será utilizado
end if;

return	id_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
