CREATE OR REPLACE FUNCTION pls_obter_minutos_intervalo (dt_inicial_p timestamp, dt_final_p timestamp, qt_min_intervalo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_minutos_w		bigint;
ie_retorno_w		varchar(1) := 'S';


BEGIN

Begin
	if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
		ds_minutos_w := calcular_minutos( to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || to_char(coalesce(dt_inicial_p,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'), to_date(to_char(clock_timestamp(),'dd/mm/yyyy') || ' ' || to_char(coalesce(dt_final_p,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'), 0, ds_minutos_w);
	else
		ds_minutos_w := calcular_minutos( coalesce(dt_inicial_p,clock_timestamp()), coalesce(dt_final_p,clock_timestamp()), qt_min_intervalo_p, ds_minutos_w);
	end if;
exception
when others then
	ie_retorno_w := 'N';
	goto final;
end;

if (ds_minutos_w < 0) then
	if (-qt_min_intervalo_p > ds_minutos_w ) then
		ie_retorno_w := 'N';
	end if;
else
	if (qt_min_intervalo_p < ds_minutos_w ) then
		ie_retorno_w := 'N';
	end if;
end if;

<<final>>
null;

return	ie_retorno_w;

end;
