CREATE OR REPLACE FUNCTION pls_obter_motivo_inclusao_req ( nr_seq_motivo_inc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

begin
select	max(substr(ds_motivo_inclusao,1,255))
into STRICT	ds_retorno_w
from	pls_motivo_inc_requisicao
where	nr_sequencia	= nr_seq_motivo_inc_p;
exception
when others then
	ds_retorno_w	:= '';
end;

return	ds_retorno_w;

end;
$body$
