CREATE OR REPLACE FUNCTION pls_obter_motivo_prorrog_guia ( nr_seq_guia_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_w			varchar(255);


BEGIN

begin
		select	ds_motivo
		into STRICT	ds_motivo_w
		from	pls_guia_plano x,
				pls_motivo_prorrogacao_aut y
		where	x.nr_sequencia		 			= nr_seq_guia_p
		and		x.nr_seq_motivo_prorrogacao		= y.nr_sequencia;
exception
when others then
		ds_motivo_w := null;
end;

return	ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
