CREATE OR REPLACE FUNCTION pls_obter_motivo_susp_seg (nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= null;


BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	substr(obter_descricao_padrao_pk('PLS_MOTIVO_SUSP_SEG','DS_MOTIVO','NR_SEQUENCIA',nr_seq_motivo_susp),1,255)
	into STRICT	ds_retorno_w
	from	pls_segurado_suspensao a
	where	a.nr_seq_segurado	= nr_seq_segurado_p
	and	coalesce(a.dt_fim_suspensao::text, '') = '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
