CREATE OR REPLACE FUNCTION pls_obter_mot_rejeicao_cart ( nr_seq_motivo_rejeicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter os dados da PLS_MOT_REJEICAO_CARTEIRA
ie_opcao_p:
DS - descrição do motivo de rejeição
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);
ds_motivo_rejeicao_w	pls_mot_rejeicao_carteira.ds_motivo_rejeicao%type;


BEGIN

select	max(ds_motivo_rejeicao)
into STRICT	ds_motivo_rejeicao_w
from	pls_mot_rejeicao_carteira
where	nr_sequencia	= nr_seq_motivo_rejeicao_p;

if (ie_opcao_p = 'N') then
	ds_retorno_w 	:= ds_motivo_rejeicao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
