CREATE OR REPLACE FUNCTION pls_obter_movimentacao_benef ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_retorno_w	varchar(1);
dt_contratacao_w	timestamp;
dt_rescisao_w		timestamp;


BEGIN

select	dt_contratacao,
	dt_rescisao
into STRICT	dt_contratacao_w,
	dt_rescisao_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (trunc(dt_contratacao_w,'Month') = trunc(clock_timestamp(),'Month')) then
	ie_tipo_retorno_w	:= 'I';
elsif (coalesce(dt_rescisao_w::text, '') = '') then
	ie_tipo_retorno_w	:= 'A';
elsif (dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') then
	ie_tipo_retorno_w	:= 'E';
end if;

return	ie_tipo_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
