CREATE OR REPLACE FUNCTION pls_obter_msg_boleto_sbc ( nr_seq_pagador_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_plano_w		bigint;
ie_tipo_contratacao_w	varchar(2);
nr_protocolo_ans_w	varchar(20);
nr_seq_contrato_w	bigint;
ds_estipulante_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN 
 
select	max(a.nr_seq_plano), 
	max(b.nr_seq_contrato) 
into STRICT	nr_seq_plano_w, 
	nr_seq_contrato_w 
from	pls_segurado		a, 
	pls_contrato_pagador	b 
where	a.nr_seq_pagador	= b.nr_sequencia 
and	b.nr_sequencia		= nr_seq_pagador_p;
 
select	ie_tipo_contratacao, 
	nr_protocolo_ans 
into STRICT	ie_tipo_contratacao_w, 
	nr_protocolo_ans_w 
from	pls_plano 
where	nr_sequencia	= nr_seq_plano_w;
 
select	obter_nome_pf_pj(cd_pf_estipulante,cd_cgc_estipulante) 
into STRICT	ds_estipulante_w 
from	pls_contrato 
where	nr_sequencia	= nr_seq_contrato_w;
 
if (ie_tipo_contratacao_w = 'I') then 
	ds_retorno_w	:= 'Plano Individual/Familiar  ANS '||substr(nr_protocolo_ans_w,1,3)||'.'||substr(nr_protocolo_ans_w,4,3)||'/'||substr(nr_protocolo_ans_w,7,2)||'-'||substr(nr_protocolo_ans_w,9,1);
elsif (ie_tipo_contratacao_w = 'CA') then 
	ds_retorno_w	:= 'Plano coletivo por ades√£o - Contrat.: '||ds_estipulante_w;
elsif (ie_tipo_contratacao_w = 'CA') then 
	ds_retorno_w	:= 'Plano coletivo empresarial - Contrat.: '||ds_estipulante_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
