CREATE OR REPLACE FUNCTION pls_obter_msg_taxa_difelidade ( ie_taxa_difelidade_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_sequencia_w			bigint;
ds_evento_w			varchar(100);


BEGIN
if (ie_taxa_difelidade_p	= 'S') then
	begin
	select	nr_sequencia,
		ds_evento
	into STRICT	nr_sequencia_w,
		ds_evento_w
	from	pls_evento
	where	ie_taxa_difelidade	= 'S'
	and	nr_sequencia		<> nr_sequencia_p;
	exception
	when others then
		nr_sequencia_w	:= null;
		ds_evento_w	:= null;
	end;
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		ds_retorno_w	:= 'JÃ¡ existe o evento de taxa de fidelidade: (' || nr_sequencia_w || ' - ' || ds_evento_w || ')';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
