SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nivel_documento ( cd_classificacao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		bigint;
k			bigint;


BEGIN
vl_retorno_w			:= 1;
if (cd_classificacao_p IS NOT NULL AND cd_classificacao_p::text <> '') then
	FOR k in 1..length(cd_classificacao_p) LOOP
		if (substr(cd_classificacao_p,k,1) = '.') then
			vl_retorno_w	:= vl_retorno_w + 1;
		end if;
	END LOOP;
end if;
return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
