CREATE OR REPLACE FUNCTION pls_obter_nivel_lib_auditor ( nr_seq_grupo_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			integer := null;
nr_seq_nivel_lib_w		bigint;


BEGIN


begin
	select	a.nr_seq_nivel_lib
	into STRICT	nr_seq_nivel_lib_w
	from	pls_membro_grupo_aud a,
		pls_grupo_auditor b
	where	a.nr_seq_grupo = nr_seq_grupo_p
	and	a.nm_usuario_exec = nm_usuario_p
	and	a.nr_seq_grupo = b.nr_sequencia;
exception
when others then
	nr_seq_nivel_lib_w := 0;
end;

if (coalesce(nr_seq_nivel_lib_w,0) = 0) then

	begin
		select	nr_seq_nivel_lib
		into STRICT	nr_seq_nivel_lib_w
		from	pls_grupo_auditor
		where	nr_sequencia = nr_seq_grupo_p;
	exception
	when others then
		nr_seq_nivel_lib_w := 0;
	end;
end if;

if (coalesce(nr_seq_nivel_lib_w,0) > 0) then

	begin
	select	nr_nivel_liberacao
	into STRICT	nr_retorno_w
	from	pls_nivel_liberacao
	where	nr_sequencia = nr_seq_nivel_lib_w;
	exception
	when others then
		nr_retorno_w := null;
	end;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
