SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nivel_regra_grup_aud ( nr_seq_grupo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_classificacao_w		bigint;
ie_tipo_auditoria_w		smallint;
nr_retorno_w			bigint := null;


BEGIN

select	nr_seq_classificacao,
	ie_tipo_auditoria
into STRICT	nr_seq_classificacao_w,
	ie_tipo_auditoria_w
from	pls_grupo_auditor
where	nr_sequencia = nr_seq_grupo_p;


if (ie_tipo_auditoria_w = 2) and (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '') then
	select	max(nr_nivel_liberacao)
	into STRICT	nr_retorno_w
	from	pls_nivel_liberacao
	where	nr_sequencia in (SELECT	nr_seq_nivel_lib
				from	pls_regra_geracao_grupo
				where	nr_seq_classificacao = nr_seq_classificacao_w
				and	ie_situacao = 'A');

end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_nivel_regra_grup_aud ( nr_seq_grupo_p bigint) FROM PUBLIC;

