SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nm_espec_prest ( nr_seq_prestador_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


cd_especialidade_w		varchar(4000)	:= null;
ds_especialidades_w		varchar(4000);

C01 CURSOR FOR
	SELECT	ds_valor_vchr2 cd_especialidades
	from	table(pls_util_pck.converter_lista_valores(cd_especialidade_w, ','));

BEGIN

cd_especialidade_w	:= substr(pls_obter_cod_espec_prest(nr_seq_prestador_p,cd_medico_p),1,400);


for r_c01 in c01 loop

	if (length(ds_especialidades_w) > 3900) then
		exit;
	end if;

	begin
	ds_especialidades_w	:= ds_especialidades_w || Obter_Desc_Espec_medica(r_c01.cd_especialidades) || '\ ';
	exception
	when others then
		exit;
	end;

end loop;


return	substr(ds_especialidades_w,1,length(ds_especialidades_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
