SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nm_prest ( cd_prestador_p text) RETURNS varchar AS $body$
DECLARE

					 
/* Function usada para atualizar o campo descritivo de um WDLG com o nome do prestador, 
recebe como parâmetro o código que retorna do localizador*/
 
 
ds_retorno_w			varchar(255);
nr_seq_prestador_w		pls_prestador.nr_sequencia%type;


BEGIN 
 
begin 
select	max(nr_sequencia) 
into STRICT	nr_seq_prestador_w 
from	pls_prestador 
where	cd_prestador = cd_prestador_p;
exception when no_data_found then 
	nr_seq_prestador_w	:= 0;
end;
 
if (nr_seq_prestador_w > 0) then 
	ds_retorno_w	:= substr(pls_obter_dados_prestador(nr_seq_prestador_w,'N'),1,255);
else 
	ds_retorno_w	:= '';
end if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
