SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nomes_contrato ( nr_seq_parametro_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_pessoa_w		varchar(20);

/*	'P' - Pagador
	'VC' - vendedor canal
	'V' - Vendedor PF */
BEGIN
ds_retorno_w	:= '';

if (nr_seq_parametro_p IS NOT NULL AND nr_seq_parametro_p::text <> '') then
	if (ie_opcao_p = 'P') then
		select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255)
		into STRICT	ds_retorno_w
		from	pls_contrato_pagador
		where	nr_sequencia	= nr_seq_parametro_p;
	elsif (ie_opcao_p = 'VC') then
		select	substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,255)
		into STRICT	ds_retorno_w
		from	pls_vendedor
		where	nr_sequencia	= nr_seq_parametro_p;
	elsif (ie_opcao_p = 'V') then
		select	substr(obter_nome_pf(cd_pessoa_fisica),1,255)
		into STRICT	ds_retorno_w
		from	pls_vendedor_vinculado
		where	nr_sequencia	= nr_seq_parametro_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
