CREATE OR REPLACE FUNCTION pls_obter_nome_arquivo_cart ( nr_seq_lote_p bigint, cd_interface_p bigint, ds_diretorio_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

/*
cd_interface_p
1956, 2243, 2720 - Unimed São José Rio Preto
2143 -Life empresarial
2155 - Ativia
2185, 2702 - Unimed Blumenau
2180 - Seisa
2205 - Unimed Litoral
2237 - Unimed Ituverava
2433 - Unimed Lençóis
2553 - Santamalia
*/
ds_retorno_w		varchar(255);
nr_seq_regra_geracao_w	bigint;
ds_regra_emissao_w	varchar(255);


BEGIN

select	max(nr_seq_regra_geracao)
into STRICT	nr_seq_regra_geracao_w
from	pls_lote_carteira
where	nr_sequencia	= nr_seq_lote_p;

if (nr_seq_regra_geracao_w IS NOT NULL AND nr_seq_regra_geracao_w::text <> '') then
	select	ds_regra
	into STRICT	ds_regra_emissao_w
	from	pls_regra_emissao_carteira
	where	nr_sequencia	= nr_seq_regra_geracao_w;
end if;

if (cd_interface_p	in (1956,2243,2720)) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||to_char(nr_seq_lote_p) ||'_'||nm_usuario_p||'_.txt';
	else
		ds_retorno_w	:= to_char(nr_seq_lote_p) ||'_'||nm_usuario_p||'_.txt';
	end if;
elsif (cd_interface_p in (2143,2237)) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||to_char(nr_seq_lote_p)||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.txt';
	else
		ds_retorno_w	:= to_char(nr_seq_lote_p)||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.txt';
	end if;
elsif (cd_interface_p = 2155) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||nm_usuario_p||'_'||to_char(nr_seq_lote_p) ||'_.txt';
	else
		ds_retorno_w	:= nm_usuario_p||'_'||to_char(nr_seq_lote_p) ||'_.txt';
	end if;
elsif (cd_interface_p in (2185, 2702, 2722)) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_'||to_char(nr_seq_lote_p) ||'_.csv';
	else
		ds_retorno_w	:= nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_'||to_char(nr_seq_lote_p) ||'_.csv';
	end if;
elsif (cd_interface_p = 2180) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||ds_regra_emissao_w||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.csv';
	else
		ds_retorno_w	:= ds_regra_emissao_w||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.csv';
	end if;
elsif (cd_interface_p = 2205) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy')||'_'|| to_char(nr_seq_lote_p) ||'_.csv';
	else
		ds_retorno_w	:= nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy')||'_'|| to_char(nr_seq_lote_p) ||'_.csv';
	end if;
elsif (cd_interface_p = 2433) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||to_char(nr_seq_lote_p) ||'_'||nm_usuario_p||'_.txt';
	else
		ds_retorno_w	:= to_char(nr_seq_lote_p) ||'_'||nm_usuario_p||'_.txt';
	end if;
elsif (cd_interface_p = 2553) then
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||ds_regra_emissao_w||'_'||to_char(nr_seq_lote_p)||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'.csv';
	else
		ds_retorno_w	:= ds_regra_emissao_w||'_'||to_char(nr_seq_lote_p)||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'.csv';
	end if;
else
	if (ds_diretorio_p IS NOT NULL AND ds_diretorio_p::text <> '') then
		ds_retorno_w	:= ds_diretorio_p||nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.csv';
	else
		ds_retorno_w	:= nm_usuario_p||'_'||to_char(clock_timestamp(),'ddmmyyyy') ||'_.csv';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
