SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nome_arquivo_rpc ( nr_seq_lote_p bigint, nr_seq_rpc_contrato_p bigint, nr_seq_reajuste_p bigint, nr_seq_lote_reajuste_p bigint, ie_tipo_regra_p text) RETURNS varchar AS $body$
DECLARE


/*ie_tipo_regra_p
C - Contratos do lote
L - Lote RPC
*/
ds_retorno_w			varchar(255);
ds_estrutura_nome_arquivo_w	varchar(255);
nr_seq_lote_w			bigint;
nr_seq_reajuste_w		bigint;
dt_competencia_w		timestamp;


BEGIN


select	max(nm_arquivo)
into STRICT	ds_estrutura_nome_arquivo_w
from	pls_rpc_regra_nome_arq
where	clock_timestamp() between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp())
and	ie_tipo_regra = ie_tipo_regra_p;

if (coalesce(ds_estrutura_nome_arquivo_w::text, '') = '') then
	if (ie_tipo_regra_p = 'L') then
		ds_retorno_w	:= 'RPC_'||to_char(nr_seq_lote_p);
	elsif (ie_tipo_regra_p = 'C') then
		ds_retorno_w	:= 'RPC_'||to_char(nr_seq_lote_p)||'_'|| nr_seq_reajuste_p;
	end if;
else
	select	max(dt_inicial)
	into STRICT	dt_competencia_w
	from	pls_rpc_lote
	where	nr_sequencia	= nr_seq_lote_p;

	if (coalesce(dt_competencia_w::text, '') = '') then
		if (ie_tipo_regra_p = 'C') then
			select	max(dt_reajuste)
			into STRICT	dt_competencia_w
			from	pls_reajuste
			where	nr_sequencia	= nr_seq_reajuste_p;
		elsif (ie_tipo_regra_p = 'L') then
			select	max(dt_reajuste)
			into STRICT	dt_competencia_w
			from	pls_reajuste
			where	nr_sequencia	= nr_seq_lote_reajuste_p;
		end if;
	end if;

	ds_retorno_w := substr(replace_macro(ds_estrutura_nome_arquivo_w,'@ANO',to_char(dt_competencia_w,'yyyy')),1,255);
	ds_retorno_w := substr(replace_macro(ds_retorno_w,'@MES',to_char(dt_competencia_w,'mm')),1,255);
	ds_retorno_w := substr(replace_macro(ds_retorno_w,'@DIA',to_char(dt_competencia_w,'dd')),1,255);
	ds_retorno_w := substr(replace_macro(ds_retorno_w,'@NR_SEQ_LOTE',nr_seq_lote_p),1,255);
	ds_retorno_w := substr(replace_macro(ds_retorno_w,'@NR_SEQ_REAJUSTE',nr_seq_reajuste_p),1,255);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
