CREATE OR REPLACE FUNCTION pls_obter_nome_arq_cobr_esc ( nm_arquivo_p text, nr_arquivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nm_arquivo_w	varchar(255);
ds_extencao_w	varchar(255);
BEGIN
FOR i IN REVERSE length(nm_arquivo_p)..1 loop
	nm_arquivo_w := nm_arquivo_w || substr(nm_arquivo_p,i,1);
end loop;

select	substr(nm_arquivo_w,1,position('.' in nm_arquivo_w))
into STRICT	ds_retorno_w
;

FOR i IN REVERSE length(ds_retorno_w)..1 loop
	ds_extencao_w := ds_extencao_w || substr(ds_retorno_w,i,1);
end loop;

ds_retorno_w := replace(nm_arquivo_p,ds_extencao_w,'') || to_char(nr_arquivo_p) || ds_extencao_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
