SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nome_benef_remessa ( nr_seq_pagador_p bigint, nr_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


nm_beneficiario_w		varchar(80);
nm_benef_ret_w			varchar(80)	:= null;
nr_ordem_w			bigint	:=0;
ie_ordem_w			bigint;

c01 CURSOR FOR
	SELECT	1 ie_ordem,
		substr(b.nm_pessoa_fisica,1,60) nm_pessoa_benef
	from	pls_contrato_pagador c,
		pessoa_fisica	b,
		pls_segurado	a
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
	and	c.nr_seq_contrato	= a.nr_seq_contrato
	and	c.nr_sequencia		= nr_seq_pagador_p
	
union all

	SELECT	2 ie_ordem,
		substr(b.nm_pessoa_fisica,1,60) nm_pessoa_benef
	from	pessoa_fisica	b,
		pls_segurado	a
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.nr_seq_pagador	= nr_seq_pagador_p
	and	not exists (select	1
				from	pls_contrato_pagador x
				where	x.nr_seq_contrato	= a.nr_seq_contrato
				and	x.cd_pessoa_fisica	= a.cd_pessoa_fisica)
	order by ie_ordem;


BEGIN
open c01;
loop
fetch c01 into
	ie_ordem_w,
	nm_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	nr_ordem_w := nr_ordem_w + 1;

	if (nr_ordem_w = nr_ordem_p) then
		nm_benef_ret_w	:=	nm_beneficiario_w;
	end if;
	end;
end loop;
close c01;

return	nm_benef_ret_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
