CREATE OR REPLACE FUNCTION pls_obter_nome_cooperativa (cd_cooperativa_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_congere_w	bigint;
ds_nome_pf_pj_w		varchar(255);


BEGIN
if (cd_cooperativa_p IS NOT NULL AND cd_cooperativa_p::text <> '') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_congere_w
	from	pls_congenere
	where	cd_cooperativa = cd_cooperativa_p;

	if (nr_seq_congere_w IS NOT NULL AND nr_seq_congere_w::text <> '') then
		begin
		select	substr(obter_nome_pf_pj(null,cd_cgc),1,80)
		into STRICT	ds_nome_pf_pj_w
		from	pls_congenere
		where	nr_sequencia = nr_seq_congere_w;
		end;
	end if;
end;
end if;

return	ds_nome_pf_pj_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
