CREATE OR REPLACE FUNCTION pls_obter_nome_operadora_estab ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

begin
select	substr(obter_razao_social(cd_cgc_outorgante),1,255)
into STRICT	ds_retorno_w
from	pls_outorgante
where	cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	ds_retorno_w	:= '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
