CREATE OR REPLACE FUNCTION pls_obter_nome_operadora ( cd_cooperativa_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= null;


BEGIN

if (cd_cooperativa_p IS NOT NULL AND cd_cooperativa_p::text <> '') then
	begin
	select	max(b.ds_razao_social)
	into STRICT	ds_retorno_w
	from	pessoa_juridica	b,
		pls_congenere	a
	where	a.cd_cgc		= b.cd_cgc
	and	(a.cd_cooperativa)::numeric 	= (cd_cooperativa_p)::numeric;
	exception
	when others then
		ds_retorno_w	:= '';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
