CREATE OR REPLACE FUNCTION pls_obter_nome_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter nome pagador pf/pj
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255) := '';


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin

		select	max(nm_pessoa_fisica)
		into STRICT	ds_retorno_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception
	when others then
		ds_retorno_w := '';
	end;
elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
		select	substr(obter_dados_pf_pj('',cd_cgc_p,'N'),1,255)
		into STRICT	ds_retorno_w
		;
	exception
	when others then
		ds_retorno_w := '';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
