CREATE OR REPLACE FUNCTION pls_obter_nome_produto_contr ( nr_seq_plano_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ie_nome_plano_contr_w		varchar(10);


BEGIN

ie_nome_plano_contr_w	:= coalesce(obter_valor_param_usuario(1202, 47, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 'N');

if (ie_nome_plano_contr_w = 'S') then
	select	substr(nm_fantasia || CASE WHEN coalesce(nr_protocolo_ans::text, '') = '' THEN ' - SCPA: '|| cd_scpa  ELSE ' - Protocolo ANS: ' || nr_protocolo_ans END ,1,255)
	into STRICT	ds_retorno_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_p;
else
	select	substr(ds_plano,1,255)
	into STRICT	ds_retorno_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
