CREATE OR REPLACE FUNCTION pls_obter_nome_produto ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w	integer;
ie_valor_param_w		varchar(1);
nm_usuario_w		varchar(15);
ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	select	wheb_usuario_pck.get_cd_estabelecimento,
		wheb_usuario_pck.get_nm_usuario
	into STRICT	cd_estabelecimento_w,
		nm_usuario_w
	;

	select	obter_valor_param_usuario(1202, 47, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w)
	into STRICT	ie_valor_param_w
	;

	if (ie_valor_param_w = 'S') then
		select	substr(pls_obter_dados_produto(nr_sequencia_p,'NF'),1,255)
		into STRICT	ds_retorno_w
		;
	else
		select	substr(pls_obter_dados_produto(nr_sequencia_p,'N'),1,255)
		into STRICT	ds_retorno_w
		;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
