CREATE OR REPLACE FUNCTION pls_obter_nome_titular (nr_seq_titular_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

/*	T - Titular
	S - Nome pr√≥prio*/
BEGIN
ds_retorno_w	:= '';

if (ie_opcao_p = 'T') then
	select	b.nm_pessoa_fisica
	into STRICT	ds_retorno_w
	from	pessoa_fisica	b,
		pls_segurado	a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	a.nr_sequencia		= nr_seq_titular_p
	and	coalesce(a.nr_seq_titular::text, '') = '';
elsif (ie_opcao_p = 'S') then
	select	b.nm_pessoa_fisica
	into STRICT	ds_retorno_w
	from	pessoa_fisica	b,
		pls_segurado	a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	a.nr_sequencia		= nr_seq_titular_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
