CREATE OR REPLACE FUNCTION pls_obter_nome_usuario (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nm_completo_w		varchar(100);



BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
		select	ds_usuario
		into STRICT	nm_completo_w
		from 	usuario
		where 	nm_usuario	= nm_usuario_p;
	exception
	when others then
		begin
			select 	substr(obter_nome_pf(cd_pessoa_fisica_resp),1,100)
			into STRICT	nm_completo_w
			from	pls_usuario_web
			where 	NM_USUARIO_WEB = nm_usuario_p;
		exception
		when others then
			nm_completo_w := '';
		end;
	end;
end if;
return	nm_completo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
