CREATE OR REPLACE FUNCTION pls_obter_nome_usu_prop ( nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE

    nm_completo_w pls_solic_credenciame_user.nm_pessoa%type;

BEGIN
    nm_completo_w := '';
    if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
        select max(ds_usuario)
        into STRICT   nm_completo_w
        from   usuario
        where  nm_usuario = nm_usuario_p;

        if (coalesce(nm_completo_w::text, '') = '' or nm_completo_w = '') then
            select max(nm_pessoa)
            into STRICT   nm_completo_w
            from   pls_solic_credenciame_user
            where  nm_usuario = nm_usuario_p;
        end if;
    end if;

    return nm_completo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
