CREATE OR REPLACE FUNCTION pls_obter_nome_vend_vinculado ( nr_seq_vendedor_vinculado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	substr(obter_nome_pf(a.cd_pessoa_fisica),1,255)
into STRICT	ds_retorno_w
from	pls_vendedor_vinculado	b,
	pessoa_fisica		a
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	b.nr_sequencia		= nr_seq_vendedor_vinculado_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
