SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_nome_xml_diops ( nr_seq_operadora_w bigint, ie_tipo_transacao_w bigint) RETURNS varchar AS $body$
DECLARE


nm_retorno_w			varchar(255);
ds_data_w			varchar(255);


BEGIN

select	coalesce(max(cd_ans),'')
into STRICT	nm_retorno_w
from	pls_outorgante
where	nr_sequencia	= nr_seq_operadora_w;

if (ie_tipo_transacao_w	= 1) then
	nm_retorno_w	:= nm_retorno_w || '_CADASTRAL';
elsif (ie_tipo_transacao_w	= 2) then
	nm_retorno_w	:= nm_retorno_w || '_FINANCEIRO';
elsif (ie_tipo_transacao_w	= 3) then
	nm_retorno_w	:= nm_retorno_w || '_COMPLEMENTAR';
end if;

select	to_char(clock_timestamp(), 'yyyy-mm-dd_hh24miss')
into STRICT	ds_data_w
;

nm_retorno_w	:= nm_retorno_w || '_' || ds_data_w || '.xml';

return	nm_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
