CREATE OR REPLACE FUNCTION pls_obter_nome_xml_sip ( nr_seq_lote_sip_p bigint) RETURNS varchar AS $body$
DECLARE


cd_registro_ans_w		varchar(6);
ds_periodo_inicial_w		varchar(255);
nm_retorno_w			varchar(255);
ds_data_w			varchar(255);


BEGIN

select	cd_registro_ans,
	to_char(dt_periodo_inicial, 'ddmmyyyy'),
	to_char(clock_timestamp(), 'ddmmyyyyhh24miss')
into STRICT	cd_registro_ans_w,
	ds_periodo_inicial_w,
	ds_data_w
from	pls_lote_sip
where	nr_sequencia	= nr_seq_lote_sip_p;

nm_retorno_w	:= cd_registro_ans_w || '_' || ds_periodo_inicial_w || '_' || ds_data_w;

return	nm_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
