CREATE OR REPLACE FUNCTION pls_obter_notas_contestadas ( nr_fatura_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_nota_w	ptu_questionamento.nr_nota%type;
ds_retorno_w	varchar(255) := null;

C01 CURSOR FOR
	SELECT	a.nr_nota
	from	ptu_questionamento a,
		ptu_camara_contestacao b
	where	a.nr_seq_contestacao = b.nr_sequencia
	and (SELECT		max(c.cd_motivo)
		 from		ptu_motivo_questionamento c
		 where		c.nr_sequencia = a.nr_seq_mot_questionamento) <> 99
	and	b.nr_fatura = nr_fatura_p
	group by a.nr_nota;


BEGIN

open C01;
loop
fetch C01 into
	nr_nota_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	ds_retorno_w || CASE WHEN coalesce(ds_retorno_w::text, '') = '' THEN  ''  ELSE ',' END  || nr_nota_w
	into STRICT	ds_retorno_w
	;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
